let images=document.querySelectorAll(".fullscreen .img-slider"),current=0;function slider(){for(let e=0;e<images.length;e++)images[e].classList.add("opacity0");images[current].classList.remove("opacity0")}slider(),document.querySelector(".btn-upp").onclick=function(){current-1==-1?current=images.length-1:current--,slider()},document.querySelector(".btn-downn").onclick=function(){current+1==images.length?current=0:current++,slider()};